/**
 * 'x' mark shoq policy.
 */
declare enum TabShowClosePolicy {
  /**
   * Don't show.
   */
  atbxShowNone = 0,

  /**
   * Show on all tabs.
   */
  atbxShowAll = 1,

  /**
   * Show on active tab only. 
   */
  atbxShowActive = 2,

  /**
   * Show on evenry tab when mouse over.
   */
  atbxShowMouseOver = 3
}
